const Like = () => {
    const [liked, setLiked] = React.useState('');
    const onChange = event => setLiked(event.target.value);
    return (
      <div>
        <h1>Hello React Function Component!</h1>
        <button class="btn btn-info" onClick={setLiked} />
        <p>{liked}</p>
      </div>
    );
  };


export default Like;
