import React, { Component, useState, useEffect } from 'react';


const Like = () => {
    const [liked, setLiked] = React.useState('true');

    useEffect(() => {
        document.title = 'hii';
    }, [liked]);
    
 
        return (
            <div class="col-6">
                <h1>My like button!!!</h1>
                <p>You clicked {liked.valueOf()} times.</p>
                <button class="btn btn-info" onClick={() => setLiked(!liked)}>
                {liked ? 'Like' : 'Liked'}
                </button>
            </div>
        )
    

    

   
     

    

  };


export default Like;
