import React, { Component, useState, useEffect } from 'react';

function Like() {
    const [liked, setLiked] = useState(null);

    useEffect(() => {
        
        document.title = 'Liked? = {liked}'

        return () => {
            <div>
                <p>Like clicked = {liked}</p>
                <button class="btn" onClick={() =>
                setLiked(liked = true)}>
                    {liked}
                    
                </button>

                
            </div>
        };
    }, [input])


}

export default Like;
